def get_icon(name: str, size: str = "1em") -> str:
    """
    获取指定的 SVG 图标
    
    Args:
        name: 图标名称
        size: 图标大小，默认 "1em"
    
    Returns:
        SVG 图标的 HTML 字符串
    
    Example:
        ```python
        from core.utils.icon_library import get_icon
        
        # 在文本中使用
        st.markdown(f"{get_icon('check')} 成功", unsafe_allow_html=True)
        
        # 在按钮中使用
        if st.button(f"{get_icon('trash')} 删除", unsafe_allow_html=True):
            ...
        ```
    """
    icon_html = ICONS.get(name, "")
    if size != "1em" and icon_html:
        icon_html = icon_html.replace('width="1em" height="1em"', f'width="{size}" height="{size}"')
    return icon_html


def icon(name: str) -> str:
    """get_icon 的简写版本"""
    return get_icon(name)

